()=>{	
	try{		
		var node1=document.querySelector("#imgDiv > table > tbody > tr:nth-child(4) > td:nth-child(1)");
		var index1=node1.innerHTML.indexOf('<b>Address:</b>');
		var index11=node1.innerHTML.indexOf(' United States<br>');
		var index2=node1.innerHTML.indexOf('<b>ZIP:</b>');
		var index3=node1.innerHTML.indexOf('<b>Purchase Date:</b>');
		var line1=node1.innerHTML.slice(0,index1+15+10)+'<c style="filter:blur(3px)">'+node1.innerHTML.slice(index1+15+10,index11-1)+'</c>'+node1.innerHTML.slice(index11-1,index2);
		var line2=node1.innerHTML.slice(index2,index2+11)+'<c style="filter:blur(3px)">'+node1.innerHTML.slice(index2+11,index3)+'</c>';
		var line3=node1.innerHTML.slice(index3);
		node1.innerHTML=line1+line2+line3;
		return 1;
	}catch(error)
	{
		return 0;
	}

}

