function oChange(num)
{
		document.getElementById('nav-packard-glow-loc-icon').click();//open dialog	
		setTimeout(function()
		{
			document.getElementById('GLUXChangePostalCodeLink').click(); //click change
		},2000);	
		setTimeout(function(){
			document.getElementById('GLUXZipUpdateInput').value=num; //input num
		},4000);	
		setTimeout(function(){
		document.getElementById('GLUXZipUpdate-announce').click();//change
		},6000);		
		setTimeout(function(){
		var sbtn=document.querySelector('button[name="glowDoneButton"]');//apply
		if(sbtn==null)
		{
			sbtn=document.getElementById('GLUXConfirmClose');
		}
		if(sbtn!=null)
		{
			sbtn.click()
		}
		},8000);	
}

function oChange(num)
{
		document.getElementById('nav-packard-glow-loc-icon').click();//open dialog	
		setTimeout(function()
		{
			document.getElementById('GLUXCountryList').click();  //click change
		},2000);	
		setTimeout(function(){
			document.getElementById(num).click();
		},4000);	

		setTimeout(function(){
		var sbtn=document.querySelector('button[name="glowDoneButton"]');//apply
		if(sbtn==null)
		{
			sbtn=document.getElementById('GLUXConfirmClose');
		}
		if(sbtn!=null)
		{
			sbtn.click()
		}
		},6000);	
}

()=>{
	try{
		var clicknum=Math.round(Math.random()*10);		
		var list=document.querySelectorAll('#rhf-container  a.a-button.a-button-image.a-carousel-button.a-carousel-goto-nextpage');
		if(list.length>2)
		{
			if(clicknum==1)
			{
				list[0].click();
			}
			if(clicknum==2)
			{			
				list[1].click();
			}
		}
		var asin7=document.querySelector('.rhf-footer [aria-posinset="7"]');
		if(asin7!=null)
		{
			asin7.remove();
		}
		return 1;
	}catch(ex)
	{	
		console.log(ex);
		return 0;
	}
}
